function palindromeChecker (word) {
  let reference = ''
  for(let i = word.length - 1; i >= 0; i --) {
    reference += word[i]
  }

  if(word === reference) {
    return true
  }

  return false
}

console.log(palindromeChecker('level'))

function sumOfDigits(numbers) {
  let stringify = numbers.toString();
  let sum = 0;

  for (let i = 0; i < stringify.length; i++) {
    let curr = parseInt(stringify[i], 10);
    sum += curr;
  }

  return sum;
}

console.log(sumOfDigits(4282)); // Output: 16

function reversal () {
  let hello = 'hello'
  let algo = 'algorithm'
  let algo_rev = ''
  let hello_rev = ''

  for(let i = hello.length - 1; i >= 0; i --) {
    hello_rev += hello[i]
  }
  for(let i = algo.length - 1; i >= 0; i --) {
    algo_rev += algo[i]
  }

  return [hello_rev, algo_rev]
}

