function findMinNum (array) {
  let minNum = array[0];

  for(let i = 0;  i < array.length; i ++) {
    let curr = array[i]
    let prev = array[i  - 1]

    if(prev === undefined) {
      continue;
    } else if(curr < prev) {
      minNum = curr
    } else {
      continue;
    }
  }  

  return minNum
}

